<script lang="ts">
import { defineComponent, ref } from 'vue'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

export const ImageWithFallback = defineComponent({
  name: 'ImageWithFallback',
  props: {
    src: { type: String, required: false },
    alt: { type: String, required: false },
    className: { type: String, required: false },
    style: { type: [Object, String], required: false },
  },
  setup(props, { attrs }) {
    const didError = ref(false)

    const handleError = () => {
      didError.value = true
    }

    return { didError, handleError, attrs, ERROR_IMG_SRC }
  },
})

export default ImageWithFallback
</script>

<template>
  <div v-if="didError" :class="`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`" :style="style">
    <div class="flex items-center justify-center w-full h-full">
      <img :src="ERROR_IMG_SRC" alt="Error loading image" v-bind="attrs" :data-original-url="src" />
    </div>
  </div>
  <img
    v-else
    :src="src"
    :alt="alt"
    :class="className"
    :style="style"
    v-on:error="handleError"
    v-bind="attrs"
  />
</template>
